package Lec_2;

import java.util.*;
public class Loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		Scanner s=new Scanner(System.in);
		
		int n=s.nextInt();
		
		int i=1;
		
		while(i<=n) {
			
			// work
			System.out.println("hello World");
			i=i+1;
		}
		
		
	}

}
